class Solution {
    public int maxOperations(String s) {
        int n = s.length();
    int one = 0;
    int ans = 0;
    for (int i = 0; i < n; i++) {
      char c = s.charAt(i);
      if (c == '1') {
        one++;
      } else {
        // count once for burst of 0s
        if (i > 0 && s.charAt(i-1) == '1') {
          ans += one;
        }
      }
    }
    return ans;
    }
}
