class Solution {
public:
    int maxOperations(string s) {
        vector<int> ones;
        int o = 0;
        bool b = 0;
        for(int i = 0; i < s.length(); i++){
            if(s[i] == '1'){
                o++;
                b = 1;
            }
            if((s[i] == '0' || i == s.length()-1) && b){
                ones.push_back(o);
                o = 0;
                b = 0;
            }
        }
        //for(auto i : ones) cout << i << " ";
        if(s[s.length()-1] == '1') ones.pop_back();
        int ans = 0, j = ones.size();
        for(int i = 0; i < ones.size(); i++){
            ans += ones[i]*j;
            j--;
        }

        return ans;
    }
};
