class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        ones = 0
        movable = False
        for index, char in enumerate(s):
            if char == '1' or index == len(s) - 1:
                if char == '0':
                    movable = True
                if movable:
                    ans += ones
                ones += 1
                movable = False
            else:
                movable = True
        return ans
