class Solution {
public:
    int maxOperations(string s) {
        int n = s.length(), cnt_one = 0, ans = 0;
        for (int i = 0; i < n; ) {
            if (s[i] == '1') {
                ++cnt_one;
                ++i;
            }
            else {
                while (i < n && s[i] == '0') ++i;
                ans += cnt_one;
            }
        }
        return ans;
    }
};
