class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        char pre = 0;
        for (char c : s) {
            if (c == '1') {
                if (pre == '0') {
                    ans += cnt;
                }
                ++cnt;
            }
            pre = c;
        }
        if (pre == '0') {
            ans += cnt;
        }
        return ans;
    }
};
