class Solution:
    def maxOperations(self, s: str) -> int:
        count = 0
        # for i in range(s):
        i = 0
        bef = 0
        while i<len(s):
            if s[i] == '0':
                # for j in range(i,len(s)):
                j = i
                while j < len(s):
                    if s[j] != '0':
                        break
                    j+=1
                count += bef
                i = j
            else:
                bef += 1
                i+=1
        return count
