class Solution {
    fun maxOperations(s: String): Int {
        var asdf = -1;var count = 0;var res = 0;var s = s;s = s + "1"
        for (i in s.indices) {
            if (asdf != -1 && s[i] == '1') {
                var dist = i - asdf
                if (dist > 1) res += (count)}
            if (s[i] == '1') {
                asdf = i
                count++
            }
        }
        return res
    }
}
