class Solution:
    def maxOperations(self, s: str) -> int:
        one = 0
        n = len(s)
        res = 0
        for i in range(n - 1):
            if s[i] == '0' and s[i + 1] == '1':
                res += one
            elif s[i] == '1':
                one += 1
        if s[-1] == '0':
            res += one
        return res
