class Solution {
public:
    int maxOperations(string str) {
        const int size = str.size();
        int one = 0;
        int ans = 0;
        bool zero = false;
        for (int i = 0; i < size; ++i) {
            if (str[i] == '0') {
                zero = true;
            } else {
                if (zero)
                    ans += one;
                ++one;
                zero = false;
            }
        }
        if (zero)
            ans += one;
        return ans;
    }
};
