func maxOperations(values string) int {
	length := len(values) // goods
	flagGood := false
	cnts, goodsValues := 0, 0
	for i := length - 1; i >= 0; i-- {
		if values[i] == '1' && !flagGood {
			continue
		}
		if values[i] == '0' && (i == length-1 || values[i+1] != '0') {
			goodsValues++
			flagGood = true
		}
		if flagGood && values[i] == '1' {
			cnts += goodsValues
		}
	}
	return cnts
}
