class Solution {
public:
    int maxOperations(string s) {
        vector<int> A;
        int cur = 0;
        for(char& c:s){
            if(c=='1'){
                cur+=1;
            }else{
                if(cur>0)
                    A.push_back(cur);
                cur = 0;
            }
        }

        reverse(A.begin(), A.end());
        int ans = 0;
        cur = 0;
        while(A.size()) {
            ans += A.back()*A.size();
            A.pop_back();
        }
        return ans;
    }
};
