class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        vector<int> pre(n+1);
        for(int i=0;i<s.length();i++)
        {
            pre[i+1]=pre[i]+(s[i]-'0');
        }
        int j=n-1;
        long long cnt=0;
        int post=1;
        for(int i=n-1;i>=0;i--)
        {
            int k=s[i]-'0';
            if(k==0 && post==1)
                cnt+=pre[i+1];
            post=k;
        }
        return (int)cnt;
    }
};
