class Solution {
public:
    int maxOperations(string s) {
        int cnt=0,op=0;
        bool f=false;
        for(auto ch:s){
            if(ch=='1'){
                cnt++;
                f=true;
            }else if(ch=='0' && f){
                op+=cnt;
                f=!f;
            }
        }
        return op;
    }
};
