class Solution:
    def maxOperations(self, s: str) -> int:
        if s[0] == "1":
            ones = 1
        else:
            ones = 0
        ans = 0
        for i, c in enumerate(s[1:], start=1):
            if c == "1":
                ones += 1
            elif s[i-1] == "1":
                ans += ones
        return ans
