class Solution:
    def maxOperations(self, s: str) -> int:
        cnt = ans = i = 0
        n = len(s)
        while i < n:
            if s[i] == "1":
                cnt += 1
                i += 1
                continue

            while i < n and s[i] == "0":
                i += 1
            if s[i - 1] == "0":
                ans += cnt
            # print(i, cnt, ans)
        return ans
