class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int ct = 0;
        int ls = 0;
        for(int i:s){
            if(i=='1'){ct++;ls = 0;}
            else {
                if(ls==0)ans+=ct;
                ls = 1;
            }
        }
        return ans;
    }
};
