function maxOperations(s: string): number {
  const countArr: number[] = []

  let count = 0
  for (let i = 0; i < s.length; i++) {
    if (s[i] === '0' && count > 0) {
      countArr.push(count)
      count = 0
      continue
    }

    if (s[i] === '1') count++
  }

  if (count > 0) countArr.push(count)

  let res = 0

  const len = countArr.length
  for (let i = 0 ; i < countArr.length; i++) {
    res += countArr[i] * (len - i - 1)
  }

  if (s.at(-1) === '0') {
    res += countArr.reduce((sum, num) => sum + num, 0)
  }

  return res
};

