class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int sol = 0;
        for (int i = 0; i < s.size()-1; i++) {
            if (s[i] == '0' && s[i+1] == '1') {
                sol += ones;
            }
            if (s[i] == '1') ones++;
        }
        if (s.back() == '0') sol += ones;
        return sol;
    }
};
