class Solution {
public:
    int maxOperations(string s) {
        // 直接贪心模拟
        int curN = 0;
        int ans = 0;
        for (int i = 0; i < s.size() - 1; ++i) {
            if (s[i] == '1') curN++;
            if (s[i] == '1' && s[i + 1] == '0') {
                ans += curN;
            }
        }
        return ans;
    }
};
