class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        int i=0,n = s.length();

        while(i<n){
            if(s[i]=='1')
            {
                cnt++;
                i++;
                continue;
            }

            while(s[i]=='0')
            {
                i++;
            }

            ans+=cnt;
        }

        return ans;
    }
};
