class Solution {
    public int maxOperations(String s) {
        int count = 0;
        int ans = 0;
        boolean findZero = false;
        for(char c : s.toCharArray()) {
            if(c == '1') {
                if(findZero) {
                    ans += count;
                    findZero = false;
                }
                count++;
            } else {
                findZero = true;
            }
        }
        if(findZero) {
            ans += count;
        }
        return ans;
    }
}
