class Solution {
public:
    int maxOperations(string s) {
        s=s+"1";
        int n=s.size();
        bool h1=0,h0=0;
        int s1=0;
        int i=0;
        long long ans=0;
        while (s[i]=='0') i++;
        for (;i<n;++i){
            if (s[i]=='1'){
                if (h0){
                    ans+=s1;h0=0;
                }
                h1=1;s1++;
            }else{
                h0=1;
            }
        }
        return ans;
    }
};
