class Solution {
    public int maxOperations(String s) {
        int n = s.length(), i, ones = 0, result = 0;
        boolean oneEncountered = false;
        for(i = 0; i < n; i += 1) {
            if(s.charAt(i) == '0') {
                if(oneEncountered) {
                    result += ones;
                }
                oneEncountered = false;
            }
            else {
                ones += 1;
                oneEncountered = true;
            }
        }
        return result;
    }
}
