class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        for (int i = 0; i < s.length(); ) {
            if (s[i] == '1') cnt++, i++;
            else {
                ans += cnt;
                while (i < s.length() && s[i] == '0') i++;
            }
        }
        return ans;
    }
};
