class Solution {
    public int maxOperations(String s) {
        int total = 0;
        int curr = 0;
        int si = 0;
        while(si < s.length() && s.charAt(si) == '0') si++;
        for(int i = si ; i < s.length() ; i++){
            if(s.charAt(i) == '1') curr++;
            if(i > 0 && s.charAt(i) == '0' && s.charAt(i-1) == '1'){
                total += curr;
            }
        }
        return total;
    }  
}
