class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        int oneSeen = 0;
        bool zero = false;
        s += '1';
        for (int i = 0; i <= n; i++) {
            if (s[i] == '1') {
                if (zero) ans += oneSeen;
                oneSeen++;
                zero = false;
            } else zero = true;
        }

        return ans;
    }
};
