class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n=s.length();
        int c=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                c++;
            }
            else{
                if(i+1>=n || s[i+1]=='1'){
                    ans +=c;
                }
            }
        }
        return ans;
    }
};
