class Solution {
        public int maxOperations(String s) {
            int ans=0;
            int count=0;
            char[] chars = s.toCharArray();
            int i=0;
            int len = chars.length;
            int j=0;
            for(;j<len;++j){
                if(chars[j]!='1') continue;
                else break;
            }
            if(j==len) return 0;
            for(i=j;i<len;++i){
                if(chars[i]=='1'){
                    count++;
                }else if(chars[i-1]=='1'){
                    ans+=count;
                }
            }
            return ans;
        }
    }
