class Solution {
    public int maxOperations(String s) {
        char str[] = s.toCharArray();
        int count = 0 , sum = 0 , ptr = 0 , l = str.length;
        while(ptr < l){
            while(ptr < l && str[ptr] == '1'){
                count++;
                ptr++;
            }
            if(ptr<l && str[ptr] == '0'){
                sum += count;
                while(ptr <l && str[ptr] == '0'){
                    ptr++;
                }
            }
        }
        return sum;

    }
}
