class Solution:
    def maxOperations(self, s: str) -> int:
        num_ops = 0
        num1 = 0 if s[0] == "0" else 1
        for i in range(1, len(s)):
            if s[i] == "1":
                num1 += 1
            elif s[i - 1] == "1":
                num_ops += num1
        return num_ops
