class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(); 

        long long ans = 0; 
        int pref = 0; 
        for (int i = 0; i < n; i++) if (s[i] == '1') {
            if (i > 0 && s[i-1] == '1') {
                pref++; 
                continue; 
            }

            ans += pref; 
            pref++; 
        }

        if (s.back() == '0') ans += pref; 
        return ans; 
    }
};
