class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, one = 0, ze = 0, n = s.length(), i = 0;
        while(i < n)    {
            if(s[i] == '1') 
                one++, i++;
            else    {
                while(i < n && s[i] == '0') i++;
                cnt += one;
            }
        }
        return cnt;
    }
};
