class Solution {
public:
    int maxOperations(string s) {
        int currO = 0;

        reverse(s.begin(), s.end());
        s.push_back('0');

        int t = 0;
        for (char c : s) if (c == '1') t++;

        int c1 = 0;
        bool ones = true;
        int ans = 0;

        for (char c : s) {
            if (c == '1') {
                ones = true;
                t--;
            } else if (ones) {
                ans += t;
                ones = false;
            }
        }

        return ans;
    }
};
