class Solution {
public:
    int maxOperations(string s) {
        vector<int> cnt;
        cnt.push_back(0);
        for (int i = 0; i < s.length(); ++i) {
            if (s[i] == '0') {
                if (cnt[cnt.size() - 1]) {
                    cnt.push_back(0);
                }
            } else {
                cnt[cnt.size() - 1]++;
            }
        }

        int sum_c = 0;
        int sum = 0;
        for (int i = 0; i < cnt.size() - 1; ++i) {
            sum_c += cnt[i];
            sum += sum_c;
        }
        return sum;
    }
};
