class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cnt=0;
        int cnt1 = 0;


        for(int i=0; i<n; i++){
            if(s[i] == '1'){
                cnt1++;
            }
            else if(i>0 and s[i-1] != '0'){
                cnt += cnt1;
            }
        }

        return cnt;
    }
};
