class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int res = 0;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                if (i && s[i - 1] == '0') {
                    res += cnt;
                    // printf("cnt: %d, i: %d, last: %d\n", cnt, i, last);
                }
                cnt++;
            } else if (i + 1 == s.size() && s[i] == '0') {
                res += cnt;
            }
        }
        return res;
    }
};
