class Solution:
    def maxOperations(self, s: str) -> int:
        if '0' not in s:
            return 0
        grouns = []
        lo = 0
        n = len(s)
        while lo < n:
            c = 0
            while lo < n and s[lo] == '0':
                lo += 1
            while lo < n and s[lo] == '1':
                lo += 1
                c += 1
            if c > 0:
                grouns.append(c)
        if s[-1] == '1':
            grouns.pop()
        ans = 0
        for idx, val in enumerate(grouns):
            ans += val * (len(grouns) - idx)
        return ans
