class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        ans = 0
        i = 0
        pres = 0
        while i < n:
            while i < n and s[i] == '0':
                i += 1
            c = 0
            while i < n and s[i] == '1':
                i += 1
                c += 1
            if i == n:
                break
            pres += c
            ans += pres
        return ans
