class Solution {
public:
    int maxOperations(string s) {
        int n=s.length(),ans=0,ones=0;
        for(int i=0;i<n;i++){
            if(s[i]=='0'){
                int tmp=1;
                while(i+1<n && s[i+1]=='0'){
                    tmp++;
                    i++;
                }
                ans=ans+ones;
                // if(tmp>1)
                // ans=ans+ones;
            }else{
                ones++;
            }
        }
        return ans;
    }
};
