class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int cnt = 0;
        int n = s.size();
        for (int i = 0; i < n; ++i) {
            if (s[i] == '1') cnt++;
            if (i - 1 >= 0 && s[i] == '0' && s[i - 1] == '1') res +=cnt;
        }

        return res;
    }
};
