class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int cnt=0;
        int ans=0;
        int one=0;
        int have=0;
        for(int i=0;i<n;++i){
            if(s[i]=='1') {
                one++;
                have=1;
            }
            else {
                if(one>0 and have>0){
                    ans+=one;
                }
                have=0;
            }
        }
        return ans;
    }
};
