class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int val = 0;
        int n = s.length();
        bool flag = false;

        for(int i=0;i<n;i++)
        {
            if(s[i] == '1')
            {
                val++;
                flag = true;
            }
            else
            {
                if(flag) ans += val;
                    flag = false;
            }
        }
        return ans;
    }
};
