class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        string t=s;
        sort(t.begin(),t.end());
        int r=n;
        while(r>0&&t[r-1]=='1')--r;
        int ans=0;
        int sum=0;
        bool f=0;
        for(int i=0;i<n;++i)
        {
            if(s[i]=='1')++sum,f=1;
            else{
                if(f)
                ans+=sum;
                cout<<sum<<endl;
                f=0;
            }
        }
        return ans;
    }
};
