class Solution {
public:
    int maxOperations(string s) {
        int ans=0, n=s.size();
        vector<int> pos;
        for(int i=0;i<n;++i){
            if(s[i]=='1') pos.push_back(i);
        }
        pos.push_back(n);
        for(int i=0;i+1<pos.size();++i){
            if(pos[i]+1<pos[i+1]){
                ans+=(i+1);
            }
        }
        return ans;
    }
};
