class Solution {
    public int maxOperations(String s) {

        int cnt = 0;
        int res = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') cnt++;

            if (s.charAt(i) == '0' && i + 1 < s.length() && s.charAt(i + 1) == '1') {
                res += cnt;
            }
            if (s.charAt(i) == '0' && i + 1 == s.length()) {
                res += cnt;
            }

        }
        return res;

    }
}
