class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        List<Integer> a = new ArrayList<>();
        for(int i = 0; i < n; ++i) {
            if(s.charAt(i) == '1') {
                a.add(i);
            }
        }
        int m = a.size();
        if(m == 0) return 0;
        int dp[] = new int[m];
        if(a.get(m - 1) < n - 1) {
            dp[m - 1] = 1;
        }
        int ans = dp[m - 1];
        for(int i = m - 2; i >= 0; --i) {
            if(a.get(i) < a.get(i + 1) - 1) dp[i] = dp[i + 1] + 1;
            else {
                dp[i] = dp[i + 1];
            }
            ans += dp[i];
        }
        return ans;
    }
}
