class Solution
{
public:
    int maxOperations(string const &s)
    {
        int const n = s.size();
        int operation_count = 0;
        for (int i = n - 1, prev_bit = 0, zero_segment_count = 0; i >= 0; --i)
        {
            int const curr_bit = s[i];
            if (curr_bit == '0')
            {
                if (curr_bit != prev_bit)
                    ++zero_segment_count;
            }
            else
            {
                operation_count += zero_segment_count;
            }
            prev_bit = curr_bit;
        }
        return operation_count;
    }
};
