class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        string cur;cur.push_back(s[0]);
        for(int i = 1; i<n; i++){
            if(s[i]=='1')cur.push_back('1');
            else if(s[i-1]=='1')cur.push_back('0');
        }
        n = cur.size();
        vector<int> zeros(n); 
        if(cur[n-1]=='0')zeros[n-1] = 1;
        for(int i = n-2; i>=0; i--){
            zeros[i]+= zeros[i+1];
            if(cur[i]=='0')zeros[i]++;
        }
        // cout<<cur
        int ans = 0;
        for(int i = 0; i<n; i++){
            if(cur[i]=='1')
            ans += zeros[i];
        }
        return ans;
    }
};
