class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(),prev=0,ans=0,ct=0;
        for(int i=0; i<n; i++){
            if(s[i]=='1'){
                ct++;
            }else ct=0;
            if(s[i]=='1' && s[i+1]=='0'){   
                ct+=prev;
                ans+= ct;
                prev = ct;
            }
        }
        return ans;
    }
};
