class Solution {
public:
    int maxOperations(string s) {
        int cnt=0;
        int cnt2=0;
        int n=s.length();
        int ans=0;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='0')
            cnt2++;
            else{
                if(cnt2){
                    cnt++;
                    cnt2=0;
                }
                ans += cnt;
            }
        }
        return ans ;
    }
};
